$('#guo-sou-ajax').click(function(){
	console.log($(this).attr("data-action"))
	$.ajax({
		url: $(this).attr("data-action"),
		type: 'POST',
		data:{
			'number':$('#number_guo_ajax').val(),
			'Devicenumber':$('#Devicenumber_guo_ajax').val(),
			'starttime':$('#starttime_guo_ajax').val(),
			'endtime':$('#endtime_guo_ajax').val(),
			'State':$('#State_guo_ajax').val(),
			'csrfmiddlewaretoken': $('[name="csrfmiddlewaretoken"]').val()
		},
		success:function(data){
				var latest_report = JSON.parse(data);
				var tr_html = '';
				$('.guo-models-1').remove();

				for (var i = 0; i < latest_report.subclass.length; i++) {
					report_info = latest_report.subclass[i];
					tr_html += '<tr class="guo-models-1"><td class="guo-td-1">'
                                    + report_info.id + '</td><td class="guo-td-2">'
                                    + report_info.number + '</td><td class="guo-td">'
                                    + report_info.name + '</td><td class="guo-td">'
                                    + report_info.DeviceID + '</td><td class="guo-td">'
                                    + report_info.State + '</td><td class="guo-td">'
                                    + report_info.Type + '</td><td class="guo-td">'
                                    + report_info.Money + 'å…ƒ</td><td class="guo-td">'
                                    + report_info.endtime + '</td></tr>';
		}
		$('.guo-models-1').html(tr_html);
	},

	})



})